// === 1) –î–æ—Å—Ç–∞—ë–º URL –∫–∞—Ä—Ç–∏–Ω–∫–∏ –∏–∑ –ø–æ–ª—è imageUrl ===
function pickImageUrl(items) {
  for (const it of items) {
    const j = it?.json ?? {};

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ–ª–µ imageUrl (–æ—Å–Ω–æ–≤–Ω–æ–π –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç)
    if (typeof j.imageUrl === 'string' && j.imageUrl.trim()) {
      return j.imageUrl.trim();
    }

    // Fallback –Ω–∞ –¥—Ä—É–≥–∏–µ –ø–æ–ª—è (–Ω–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π)
    if (typeof j.url === 'string' && j.url.trim()) return j.url.trim();
    if (typeof j.webContentLink === 'string' && j.webContentLink.trim()) return j.webContentLink.trim();
    if (typeof j.id === 'string' && j.id.trim()) return `https://drive.google.com/uc?id=${j.id.trim()}`;
  }
  return null;
}

const imgUrl = pickImageUrl(items);

// === 2) –ü–æ–º–æ—â–Ω–∏–∫: —Ç–µ–∫—Å—Ç -> –º–∞—Å—Å–∏–≤ <p> ===
function toParagraphs(text) {
  const clean = String(text || "").replace(/^–ò—Ç–æ–≥–æ–≤—ã–π\s+—Ç–µ–∫—Å—Ç\s+—Å–∞–º–º–∞—Ä–∏:\s*/i, "");
  return clean
    .split(/\n\s*\n/)
    .flatMap(block => block.split(/\r?\n+/))
    .map(s => s.trim())
    .filter(Boolean)
    .map(s => ({ tag: "p", children: [s] }));
}

// === 3) –°–æ–±–∏—Ä–∞–µ–º –æ—Å–Ω–æ–≤–Ω–æ–π –∫–æ–Ω—Ç–µ–Ω—Ç –∏–∑ —Å—Ç—Ä–æ–∫ —Ç–∞–±–ª–∏—Ü—ã ===
const content = [];
for (const it of items) {
  const r = it.json || {};
  const hasRow = Object.prototype.hasOwnProperty.call(r, "–ö–∞–Ω–∞–ª")
              || Object.prototype.hasOwnProperty.call(r, "–†–µ–∑—É–ª—å—Ç–∞—Ç");
  if (!hasRow) continue;

  const channel = (r["–ö–∞–Ω–∞–ª"] ?? "").toString().trim();
  const result  = (r["–†–µ–∑—É–ª—å—Ç–∞—Ç"] ?? "").toString();

  if (channel) content.push({ tag: "h3", children: [channel] });
  if (result)  content.push(...toParagraphs(result));
}

// === 4) –í—Å—Ç–∞–≤–ª—è–µ–º –∫–∞—Ä—Ç–∏–Ω–∫—É –≤ –Ω–∞—á–∞–ª–æ —Å—Ç–∞—Ç—å–∏ ===
if (imgUrl) {
  content.unshift({
    tag: "figure",
    children: [{ tag: "img", attrs: { src: imgUrl } }]
  });
}

// === 5) –§–æ—Ä–º–∏—Ä—É–µ–º –∑–∞–≥–æ–ª–æ–≤–æ–∫ ===
const titleNice = `–í–µ—Å—Ç–Ω–∏–∫ –Ω–µ–¥–µ–ª–∏ ‚Äî ${new Date().toLocaleDateString("ru-RU", {
  day: "numeric", month: "long", year: "numeric"
})}`;

return [{
  json: {
    title: titleNice,
    title2: "–í–µ—Å—Ç–Ω–∏–∫ –Ω–µ–¥–µ–ª–∏",
    authorName: "Fox",
    authorUrl: "https://t.me/SMailsPub",
    content,
  }
}];

// –í—Ö–æ–¥: –Ω–µ—Å–∫–æ–ª—å–∫–æ items, —É –∫–∞–∂–¥–æ–≥–æ –º–æ–∂–µ—Ç –±—ã—Ç—å generations[0].img –∏–ª–∏ –ø—Ä–æ—Å—Ç–æ img
// –í—ã—Ö–æ–¥: 1 item { imgUrl, fileName }

function pickFirstUrl(items) {
  for (const it of items) {
    const j = it.json || {};
    // generations[].img
    if (Array.isArray(j.generations) && j.generations[0]?.img) return j.generations[0].img;
    // –∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—ã–µ –º–µ—Å—Ç–∞
    if (typeof j.img === 'string') return j.img;
    if (typeof j.url === 'string') return j.url;
  }
  return null;
}

function buildFileName(url) {
  // –ø—Ä–æ–±—É–µ–º –≤—ã—Ç–∞—â–∏—Ç—å –∏–º—è –∏ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–µ –∏–∑ URL
  try {
    const u = new URL(url);
    const base = u.pathname.split('/').pop() || ('image_' + Date.now());
    // –µ—Å–ª–∏ –Ω–µ—Ç —Ä–∞—Å—à–∏—Ä–µ–Ω–∏—è ‚Äî –ø–æ–¥—Å—Ç–∞–≤–∏–º webp/png –ø–æ –Ω–∞–ª–∏—á–∏—é
    const hasExt = /\.[a-z0-9]{3,4}$/i.test(base);
    if (hasExt) return base;
    const extFromQuery = (u.search.match(/\.(png|jpe?g|webp|gif)/i) || [,'webp'])[1];
    return base + '.' + extFromQuery.toLowerCase();
  } catch {
    return 'image_' + Date.now() + '.webp';
  }
}

const url = pickFirstUrl(items);
if (!url) {
  // –ù–∏—á–µ–≥–æ –Ω–µ –Ω–∞—à–ª–∏ ‚Äî –æ—Ç–¥–∞—ë–º –ø–æ–Ω—è—Ç–Ω–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
  return [{ json: { error: 'No image URL found in input' } }];
}

return [{
  json: {
    imgUrl: url,
    fileName: buildFileName(url)
  }
}];

// –û—á–∏—â–∞–µ–º JSON –∏ –æ—Å—Ç–∞–≤–ª—è–µ–º —Ç–æ–ª—å–∫–æ —Å—Å—ã–ª–∫—É –Ω–∞ –∫–∞—Ä—Ç–∏–Ω–∫—É
function extractImageUrl(items) {
  for (const it of items) {
    const j = it?.json ?? {};

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤—Å–µ –≤–æ–∑–º–æ–∂–Ω—ã–µ –º–µ—Å—Ç–∞, –≥–¥–µ –º–æ–∂–µ—Ç –±—ã—Ç—å —Å—Å—ã–ª–∫–∞
    if (j.data && typeof j.data.url === 'string' && j.data.url.trim()) {
      return j.data.url.trim();
    }
    if (j.data && j.data.image && typeof j.data.image.url === 'string' && j.data.image.url.trim()) {
      return j.data.image.url.trim();
    }
    if (typeof j.url === 'string' && j.url.trim()) {
      return j.url.trim();
    }
  }
  return null;
}

// –ò–∑–≤–ª–µ–∫–∞–µ–º —Å—Å—ã–ª–∫—É
const imageUrl = extractImageUrl(items);

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ç–æ–ª—å–∫–æ —á–∏—Å—Ç—ã–π –æ–±—ä–µ–∫—Ç —Å —Å—Å—ã–ª–∫–æ–π
return [{
  json: {
    imageUrl: imageUrl
  }
}];
